    }
    async getCharacters(selected) {
        let chars = [];
        for (let i = 0; i < selected.characters.length; i++) {
            const char = await this.http.get(selected.characters[i]).toPromise();
            chars.push(char);
        }
        return chars;
    }
    getCharacterById(id) {
        return this.http.get('https://swapi.dev/api/people/' + id);
    }
}
